package com.nemsolutions.aurabase.config;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.Configuration;import org.springframework.context.annotation.Import;import org.springframework.context.support.PropertySourcesPlaceholderConfigurer;import org.springframework.stereotype.Controller;import org.springframework.web.bind.annotation.RestController;import com.nemsolutions.auracore.config.HibernateConfig;import com.nemsolutions.auracore.config.JettyConfiguration;@Configuration@Import({JettyConfiguration.class, AuramaintSpringSecurityConfig.class, HibernateConfig.class})@ComponentScan(basePackages = {"com.nemsolutions"}, excludeFilters = {    @ComponentScan.Filter(RestController.class),    @ComponentScan.Filter(Controller.class),    @ComponentScan.Filter(Configuration.class)})public class WebRootConfiguration {  @Bean  public static PropertySourcesPlaceholderConfigurer propertyPlaceholderConfigurer() {    return new PropertySourcesPlaceholderConfigurer();  }}